mapper.update:
update resume set 
resume_name= #{resumeName},
update_time = #{updateTime}
where user_id = #{userId}

update resume
projectName= #{resume.projectName}, thePost=#{resume.thePost}, projectYearStart=#{resume.projectYearStart}, projectMonthStart=#{resume.projectMonthStart}, projectYearEnd=#{resume.projectYearEnd}, projectMonthEnd=#{resume.projectMonthEnd}, projectDescription=#{resume.projectDescription}, responsDescription=#{resume.responsDescription}, update_time = #{resume.updateTime}
where user_id = #{userId}

update resume
schoolName= #{resume.schoolName}, professionalName=#{resume.professionalName}, schoolYearStart=#{resume.schoolYearStart}, schoolYearEnd=#{resume.schoolYearEnd}, update_time = #{resume.updateTime}
where user_id = #{userId}

update resume 
 
	workLink=#{resume.workLink},
	workDescription=#{resume.workDescription},
	update_time = #{resume.updateTime}

where user_id = #{userId}

update resume set 
current_state= #{resume.currentState} 
where user_id = #{userId}
